// RSS'den görsel çıkarma fonksiyonu
function extractImageFromRSS(item) {
    let imageUrl = '';

    // 1. Enclosure URL (podcast/medya dosyaları için)
    if (item.enclosure && item.enclosure.url && 
        (item.enclosure.url.includes('.jpg') || item.enclosure.url.includes('.png') || 
         item.enclosure.url.includes('.jpeg') || item.enclosure.url.includes('.webp'))) {
        imageUrl = item.enclosure.url;
    }
    // 2. Media:content (RSS medya eklentisi)
    else if (item['media:content'] && item['media:content']['$'] && item['media:content']['$'].url) {
        imageUrl = item['media:content']['$'].url;
    }
    // 3. Media:thumbnail (küçük resim)
    else if (item['media:thumbnail'] && item['media:thumbnail']['$'] && item['media:thumbnail']['$'].url) {
        imageUrl = item['media:thumbnail']['$'].url;
    }
    // 4. Image field (bazı RSS formatları)
    else if (item.image && typeof item.image === 'string') {
        imageUrl = item.image;
    }
    else if (item.image && item.image.url) {
        imageUrl = item.image.url;
    }
    // 5. HTML içeriğinden img tag'lerini bul
    else if (item.content && typeof item.content === 'string') {
        const imgMatch = item.content.match(/<img[^>]+src=["']([^"'>]+)["'][^>]*>/i);
        if (imgMatch && imgMatch[1]) {
            imageUrl = imgMatch[1];
        }
    }
    // 6. Description içeriğinden img tag'lerini bul
    else if (item.description && typeof item.description === 'string') {
        const imgMatch = item.description.match(/<img[^>]+src=["']([^"'>]+)["'][^>]*>/i);
        if (imgMatch && imgMatch[1]) {
            imageUrl = imgMatch[1];
        }
    }
    // 7. ContentSnippet içeriğinden img tag'lerini bul
    else if (item.contentSnippet && typeof item.contentSnippet === 'string') {
        const imgMatch = item.contentSnippet.match(/<img[^>]+src=["']([^"'>]+)["'][^>]*>/i);
        if (imgMatch && imgMatch[1]) {
            imageUrl = imgMatch[1];
        }
    }

    return imageUrl;
}

