// Slide'ları oluştur - Düzeltilmiş
for (let i = 0; i < slideData.slides.length; i++) {
  const slide = slideData.slides[i];

  // Slide numarası HTML'i
  const slideNumberHtml = ``;

  if (slide.type === 'intro') {
    // İntro slide - Özel layout
    htmlTemplate += '<section data-background-color="#667eea">';
