    // Özet slide
    htmlTemplate += '<section>';
    htmlTemplate += slideNumberHtml;
    htmlTemplate += '<h2>' + escapeHtml(slide.title) + '</h2>';
    htmlTemplate += '<div class="slide-content">';
    htmlTemplate += slide.content;
    htmlTemplate += '</div>';
    htmlTemplate += '</section>';
