      // URL doğrulama
      if (!safeImageUrl || typeof safeImageUrl !== 'string' || safeImageUrl.length < 10 || 
          (!safeImageUrl.startsWith('https://') && !safeImageUrl.startsWith('http://'))) {
        safeImageUrl = defaultTechImages[0];
        console.log(`Invalid image URL detected: ${slide.imageUrl}, using fallback`);
